document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector(".icon-menu"),t=document.querySelector(".menu__body");e.addEventListener("click",()=>{e.classList.toggle("icon-menu_active"),t.classList.toggle("menu__body_active")});const o=document.querySelector(".slider__row"),c=document.querySelectorAll(".slider__content"),n=document.querySelectorAll(".dots__item");n.forEach((e,t)=>{e.addEventListener("click",e=>{let s=c[0].offsetWidth;var d;o.style.transform=`translateX(${-s*t}px)`,d=t,n.forEach(e=>{e.classList.remove("dots__item_active"),n[d].classList.add("dots__item_active")})})})});